angular.module('image.effects')

.service('effects', ['$rootScope', '$http', 'canvas', 'history', function ($rootScope, $http, canvas, history) {

    var effects = {

        all: [
            { name: 'effectType1', typeNumber: 1 },
            { name: 'effectType2', typeNumber: 2 },
            { name: 'effectType3', typeNumber: 3 }
        ],

        appliedEffects: [],
        applyPHPEffect: function(effect){
            $rootScope.isLoading();
            effects.markAsApplied(effect.name);

            //need to use timeout to display loading spinner properly
            setTimeout(function() {
                canvas.fabric.forEachObject(function(obj) {                    
                    console.log(obj);
                    console.log(obj._element);
                    tmp = obj._element;
                    var d=tmp.src;
                     $http.post('./converter.php', { url: d});
                    // var w=window.open('about:blank','image from canvas');
                    // w.document.write("<img src='"+d+"' alt='from canvas'/>");
                    
                    // console.log(obj._originalElement.currentSrc);
                    // if (obj.applyEffects) {
                    //     obj.effects.push(effects.getEffect(effect));
                    //     obj.applyEffects(canvas.fabric.renderAll.bind(canvas.fabric));
                    // }
                });

                history.add('effect: '+(effect.displayName || effect.name), 'brightness-6');
                effects.lastAppliedEffect = effect;
                $rootScope.isNotLoading();
            }, 30);
        },
        applyEffect: function(effect) {
            if ( ! this.effectExists(effect)) return;

            if (this.effectAlreadyApplied(effect.name)) {
                return this.removeEffect(effect);
            }

            $rootScope.isLoading();
            effects.markAsApplied(effect.name);

            //need to use timeout to display loading spinner properly
            setTimeout(function() {
                canvas.fabric.forEachObject(function(obj) {
                    if (obj.applyEffects) {
                        obj.effects.push(effects.getEffect(effect));
                        obj.applyEffects(canvas.fabric.renderAll.bind(canvas.fabric));
                    }
                });

                history.add('effect: '+(effect.displayName || effect.name), 'brightness-6');
                effects.lastAppliedEffect = effect;
                $rootScope.isNotLoading();
            }, 30);
        },

        removeEffect: function(effect) {
            if ( ! this.effectExists(effect)) return;

            $rootScope.isLoading();
            effects.unmarkAsApplied(effect.name);

            setTimeout(function() {
                canvas.fabric.forEachObject(function(obj) {
                    if (obj.applyEffects) {
                        for (var i = 0; i < obj.effects.length; i++) {
                            if (obj.effects[i].name.toLowerCase() === effect.name.toLowerCase()) {
                                obj.effects.splice(i, 1);
                                obj.applyEffects(canvas.fabric.renderAll.bind(canvas.fabric));
                            }
                        }
                    }
                });

                effects.lastAppliedEffect = false;
                $rootScope.isNotLoading();
            }, 30);
        },

        applyValue: function(effectName, optionName, optionValue) {
            $rootScope.isLoading();

            setTimeout(function() {
                canvas.fabric.forEachObject(function(obj) {
                    if (obj.applyEffects) {
                        for (var i = 0; i < obj.effects.length; i++) {
                            var effect = obj.effects[i];

                            if (effect.type.toLowerCase() === effectName.toLowerCase()) {
                                effect[optionName] = optionValue;
                                obj.applyEffects(canvas.fabric.renderAll.bind(canvas.fabric));
                            }
                        }
                    }
                });

                $rootScope.isNotLoading();
            }, 30);
        },

        effectAlreadyApplied: function(name) {
            return this.appliedEffects.indexOf(name) !== -1
        },

        markAsApplied: function(name) {
            if (this.appliedEffects.indexOf(name) === -1) {
                this.appliedEffects.push(name);
            }
        },

        unmarkAsApplied: function(name) {
            for (var i = 0; i < this.appliedEffects.length; i++) {
                if (this.appliedEffects[i] === name) {
                    this.appliedEffects.splice(i, 1);
                }
            }
        },

        effectExists: function(effect) {
            return fabric.Image.effects[fabric.util.string.capitalize(effect.uses || effect.name, true)] !== undefined;
        },

        getEffect: function(effect) {
            var newEffect;

            if (effect.uses) {
                newEffect = new fabric.Image.effects[fabric.util.string.capitalize(effect.uses, true)]({ matrix: effect.matrix });
            }

            else {
                var options = {};

                for (var key in effect.options) {
                    options[key] = effect.options[key].current;
                }

                newEffect = new fabric.Image.effects[fabric.util.string.capitalize(effect.name, true)](options);
            }

            newEffect.name = effect.name;

            return newEffect;
        },
    };

    return effects;

}]);